export function elementSupportsDrupalElementStyles(selectedElement,modelAttributes,schema,){for(const modelAttribute of modelAttributes){if(schema.checkAttribute(selectedElement,modelAttribute)){return!0}}
return!1}
export function getClosestElementWithElementStyleAttribute(selection,schema,modelAttributes,){const selectedElement=selection.getSelectedElement();if(selectedElement&&elementSupportsDrupalElementStyles(selectedElement,modelAttributes,schema)){return selectedElement}
let{parent}=selection.getFirstPosition();while(parent){if(parent.is('element')){if(elementSupportsDrupalElementStyles(parent,modelAttributes,schema)){return parent}}
parent=parent.parent}
return null}