import{Plugin}from 'ckeditor5/src/core';import{WidgetToolbarRepository}from 'ckeditor5/src/widget';import{getClosestSelectedDrupalMediaWidget,isObject}from './utils';function normalizeDeclarativeConfig(config){return config.map((item)=>(isObject(item)?item.name:item))}
