((once)=>{Drupal.behaviors.adminToolbarHover={attach:(context)=>{if(context!==document){return}
once('admin-toolbar-hover','body',context).forEach((element)=>{element.querySelectorAll('.toolbar-tray.toolbar-tray-horizontal .menu-item.menu-item--expanded',).forEach((item)=>{item.addEventListener('mouseenter',()=>{item.closest('.toolbar-menu').querySelectorAll('.menu-item.menu-item--expanded').forEach((listItem)=>{listItem.classList.remove('hover-intent')});item.classList.add('hover-intent')});item.addEventListener('mouseleave',()=>{item.classList.remove('hover-intent')})})})},}})(once)