((once)=>{Drupal.behaviors.adminToolbarStickyBehavior={attach:(context)=>{if(context!==document){return}
once('admin-toolbar-sticky-behavior','body',context).forEach((element)=>{const delta=10;let lastScrollTop=0;window.addEventListener('scroll',()=>{if(localStorage.getItem('Drupal.adminToolbar.toggleToolbarHidden',)==='true'){return}
const{scrollTop}=document.scrollingElement;if(Math.abs(lastScrollTop-scrollTop)<=delta)return;if(scrollTop>lastScrollTop){element.classList.add('sticky-toolbar-hidden')}else{element.classList.remove('sticky-toolbar-hidden')}
lastScrollTop=scrollTop})},)},}})(once)