((Drupal,once)=>{Drupal.behaviors.simpleSitemapViewsUiArguments={attach(context){once('simple-sitemap-views-ui-arguments','fieldset.indexed-arguments',context,).forEach((element)=>{let checkboxes=element.querySelectorAll('input[type="checkbox"]');checkboxes=Array.from(checkboxes);const check=(index)=>{checkboxes.slice(0,index).forEach((checkbox)=>{checkbox.checked=!0})};const uncheck=(index)=>{checkboxes.slice(index).forEach((checkbox)=>{checkbox.checked=!1})};checkboxes.forEach((checkbox)=>{checkbox.addEventListener('change',()=>{const index=checkboxes.indexOf(checkbox);if(checkbox.checked){check(index)}else{uncheck(index)}})})})},}})(Drupal,once)