(function($,Drupal,once){Drupal.behaviors.webformTextFormat={attach:function(context){$(once('webform-text-format','.js-text-format-wrapper textarea',context)).each(function(){if(!window.CKEDITOR){return}
var $textarea=$(this);$textarea.on('change',function(){if(CKEDITOR.instances[$textarea.attr('id')]){var editor=CKEDITOR.instances[$textarea.attr('id')];editor.setData($textarea.val())}});$textarea.on('webform:disabled',function(){if(CKEDITOR.instances[$textarea.attr('id')]){var editor=CKEDITOR.instances[$textarea.attr('id')];editor.setReadOnly($textarea.is(':disabled'))}})})}}})(jQuery,Drupal,once)